import React from 'react';
import Hero from './Hero'; // Import the Hero component
import './pagecomp.css';

export default function PageComp({ title, content }) {
  return (
    <div className='pagescreen'>
      <h1>{title}</h1>
      <div className='movies-container'>
        {content.map((item) => (
          <div key={item.id} className='image-text-div'>
            <Hero
              id={item.id}
              type={item.media_type}  // Ensure that the content array includes the media_type for each item
              title={item.title || item.name} // Use either title or name depending on the type of media
              link={`https://image.tmdb.org/t/p/original/${item.poster_path}`}
            />
          </div>
        ))}
      </div>
    </div>
  );
}
