import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import './searchstyle.css';
import Home from '../Screens/Home';

export default function Search() {
  const [query, setQuery] = useState(''); // State to hold the search query
  const [results, setResults] = useState([]); // State to hold search results

  const handleSearch = async () => {
    if (!query) return; // Exit if query is empty

    const options = {
      method: 'GET',
      headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIxYTliMjRjZWU3YzI5NmIwMzFmNzBiNTc1ZGE1ODBjNyIsIm5iZiI6MTcyNDc0NjY1Ny41OTQyNjYsInN1YiI6IjY2Y2M3ODA3OWQ2MjhjZDJlYTMyOWUwNSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.RU05r6bZvPr3Tb1dB1DShOWGDNlRMvwTJx0Ko4fkQdM'
      }
    };

    try {
      const response = await fetch(`https://api.themoviedb.org/3/search/movie?query=${encodeURIComponent(query)}&include_adult=false&language=en-US&page=1`, options);
      const data = await response.json();
      setResults(data.results);
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div>
      <Navbar />
      <Home />
      <input
        className='search-box'
        placeholder='Search here'
        value={query}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
      />
      <button className='search-button' onClick={handleSearch}>Search</button>
      <div className='search-results'>
        {results.length > 0 ? (
          results.map((item) => (
            <div key={item.id} className='search-item'>
              <img
                alt={item.title}
                src={`https://image.tmdb.org/t/p/w200/${item.poster_path}`}
              />
              <h3>{item.title}</h3>
              <p>{item.overview}</p>
            </div>
          ))
        ) : (
          <p>No results found</p>
        )}
      </div>
    </div>
  );
}
