import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar';
import VideoPlayerModal from '../components/VideoPlayerModal';
import './detailpage.css';

export default function Detailpage() {
  const { id, type } = useParams();
  const [details, setDetails] = useState(null);
  const [teaserKey, setTeaserKey] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const baseUrl = 'https://api.themoviedb.org/3/';
    const endpoint = type === 'movie' ? `movie/${id}` : `tv/${id}`;
    const options = {
      method: 'GET',
      headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIxYTliMjRjZWU3YzI5NmIwMzFmNzBiNTc1ZGE1ODBjNyIsIm5iZiI6MTcyNDgyNjY1My40NzIyMzcsInN1YiI6IjY2Y2M3ODA3OWQ2MjhjZDJlYTMyOWUwNSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.uuCj87QoirPO1_j12KRNCFcRSp8k4DkoppeOSq9zK_4'
      }
    };

    fetch(`${baseUrl}${endpoint}?language=en-US`, options)
      .then(response => response.json())
      .then(data => setDetails(data))
      .catch(err => console.error(err));

    fetch(`${baseUrl}${endpoint}/videos?language=en-US`, options)
      .then(response => response.json())
      .then(data => {
        const teaser = data.results.find(video => video.type === 'Teaser');
        if (teaser) {
          setTeaserKey(teaser.key);
        }
      })
      .catch(err => console.error(err));
  }, [id, type]);

  if (!details) {
    return <div>Loading...</div>;
  }

  const handlePlayTeaser = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div>
      <Navbar />
      <div className='detaipage-design'>
        <div className='poster-intro'>
          <img
            alt='img-post'
            src={`https://image.tmdb.org/t/p/w500${details.poster_path || ''}`}
          />
          <div className='details-des'>
            <h1>{details.title || details.name || 'Title not available'}</h1>
            <h2>{details.genres?.map(genre => genre.name).join(', ') || 'Genres not available'}</h2>
            {teaserKey && (
              <button className='play_trail' onClick={handlePlayTeaser}>
                <span><i className="fa-solid fa-play" /></span>
                <span>Play Teaser</span>
              </button>
            )}
            <p>{details.overview || 'Overview not available'}</p>
            <div className='credit'>
              <h1>Created By</h1>
              <h2>{details.created_by?.map(creator => creator.name).join(', ') || 'N/A'}</h2>
            </div>
          </div>
        </div>
      </div>
      {isModalOpen && (
        <VideoPlayerModal teaserKey={teaserKey} onClose={handleCloseModal} />
      )}

      <div className='image-cast'>
        <div className='image'>
            <img alt='img'
            src=''/>
        </div>
        <div className='cast'>

        </div>
      </div>
    </div>
  );
}
