import React, { useState,useEffect } from 'react'
import './homecomp.css'

export default async function Homepagecomp() {

  const [posterUrl, setPosterUrl] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const options = {
        method: 'GET',
        headers: {
          accept: 'application/json',
          Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIxYTliMjRjZWU3YzI5NmIwMzFmNzBiNTc1ZGE1ODBjNyIsIm5iZiI6MTcyNDY3NzI5MS44MzUwMDQsInN1YiI6IjY2Y2M3ODA3OWQ2MjhjZDJlYTMyOWUwNSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.ITtBRF2mUWsLl34uH3MImoDnmFbqkM69BlMKMyTfXCg'
        }
      };

      try {
        const response = await fetch('http://api.themoviedb.org/3/movie/now_playing?language=en-US&page=1', options);
        if (response.ok) {
          const data = await response.json();
          setPosterUrl(data.results[0].poster_path); // Save the poster path to state
        } else {
          console.error('Error:', response.statusText);
        }
      } catch (e) {
        console.error('Fetch error:', e);
      }
    };

    fetchData();
  }, []);
  
  
  return (
    <div className='home'>
        {/* <iframe width="1550" height="800" src="https://www.youtube.com/embed/X1ECSmXUtAQ?si=TF8fg7FGuoHxWMNr"
         frameborder="0" 
         allowfullscreen
         ></iframe> */}

{posterUrl && (
        <img
          alt='Movie Poster'
          src={`https://image.tmdb.org/t/p/original/${posterUrl}`}
        />
      )}
        <div className='home-des'>
          <h1>Movie Name</h1>
          <p>This is movie description This is movie description. This is movie description</p>
          <div className='buttons-div'>
          <button className='playbtn'>
            <span><i class="fa-solid fa-play"/></span>
             <span>Play</span>
              </button>

            <button className='morebtn'>
            <span><i class="fa-solid fa-circle-info"></i></span>
              <span>More info</span>
              
              </button>

              <div className='vol'>
              <i class="fa-solid fa-volume-high"></i>
              </div>
            
            
          </div>
        </div>
      
    </div>
  )
}
